import React from 'react';
import './styles/cube.css';

const OneSidedDice = () => {
  return (
    <div className="dice">
      <div className="dice-face">1</div>
    </div>
  );
};

const TwentySidedDice = ({ number }) => {
  const [angle, setAngle] = React.useState(0);

  React.useEffect(() => {
    setAngle((number - 1) * 18);
  }, [number]);

  return (
    <div className="dice">
      {[...Array(20)].map((_, index) => (
        <div key={index} className="dice-face">
          {index + 1}
        </div>
      ))}
      <style>
        {`
          .dice {
            transform: rotateY(${angle}deg);
          }
        `}
      </style>
    </div>
  );
};

export { OneSidedDice, TwentySidedDice };
