import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { registerPlayer, loginPlayer } from './api';

const AuthPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true);
  const navigate = useNavigate();

  const handleAuth = async () => {
    if (isLogin) {
      const player = await loginPlayer(username, password);
      if (player) {
        localStorage.setItem('userId', JSON.stringify(player));
        navigate('/start'); // Переход на страницу StartPage
      } else {
        alert('Неправильный логин или пароль');
      }
    } else {
      const player = await registerPlayer(username, password);
      if (player) {
        localStorage.setItem('userId', JSON.stringify(player[1]));
        navigate('/start'); // Переход на страницу StartPage
      } else {
        alert('Ошибка регистрации');
      }
    }
  };

  return (
    <div>
      <h2>{isLogin ? 'Вход' : 'Регистрация'}</h2>
      <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Введите имя пользователя" />
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Введите пароль" />
      <button onClick={handleAuth}>{isLogin ? 'Войти' : 'Зарегистрироваться'}</button>
      <button onClick={() => setIsLogin(!isLogin)}>{isLogin ? 'Регистрация' : 'Вход'}</button>
    </div>
  );
};

export default AuthPage;
