import { useState, useEffect, useRef } from "react";
import { SetStartBattle } from "./api.js";

const BattlePage = ({ state }) => {
  console.log(state);
  const [battleState, setBattleState] = useState({});
  const intervalRef = useRef(null);

  useEffect(() => {
    const fetchBattleState = async () => {
      const result = await SetStartBattle(state);
      setBattleState(result);
    };

    fetchBattleState(); // initial fetch

    intervalRef.current = setInterval(fetchBattleState, 1000); // fetch every 1 second

    return () => {
      clearInterval(intervalRef.current);
    };
  }, [state]);

  return (
    <div>
      <h2>Battle Page</h2>
      <p>Battle State: {JSON.stringify(battleState)}</p>
    </div>
  );
};

export default BattlePage;
