// src/GamePage.js
import React, {useState, useEffect} from 'react';
import ButtonMove from './ButtonMove';

const GamePage = () => {
    const player = JSON.parse(localStorage.getItem('player'))
    const [location, setLocation] = useState(null)
    useEffect(()=>{
        const direction = localStorage.getItem('direction')
        if (direction){
            setLocation(direction)
        }
    },[])
  return (
    <div>
      <h2>Параметры персонажа:</h2>
      <ul>
        <li>Имя: {player.Player.Name}</li>
        <li>Здоровье: {player.Player.Health}</li>
        <li>Сила: {player.Player.Strenght}</li>
        <li>Уровень: {player.Player.Lvl}</li>
        <li>HP: {player.Player.Hp}</li>
      </ul>
      {
        location ? (
            <p>Вы находитесь в {location.Location.Name}</p>
        ):(

            <ButtonMove setLocation={setLocation} /> 
        )
      }
    </div>

  );
};

export default GamePage;
