// src/GamePage.js
import React, { useState, useEffect } from 'react';
import ButtonMove from './ButtonMove';
import { useLocation, useNavigate } from "react-router-dom";

const GamePage = () => {
  // let player = localStorage.getItem('player');
  // if (player !== undefined) {
  //   player = JSON.parse(player);
  // } else {
  //   player = {}; // or some default value
  // }
  const locations = useLocation();
  console.log(locations.Player.Name)
  const [location, setLocation] = useState(null);
  let npc = localStorage.getItem('npc');
  if (npc !== undefined) {
    npc = JSON.parse(npc);
  } else {
    npc = {}; // or some default value
  }
  const [isInCombat, setIsInCombat] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const direction = JSON.parse(localStorage.getItem('direction'));
    console.log(direction);
    if (direction ) {
      setLocation(direction);
    }
  }, []);

  const handleAcceptCombat = () => {
    setIsInCombat(true);
    const status = {
       enemyHp: npc.Health,
       enemyDamge: npc.Strenght,
        Hp:locations.Player.Health,
        damage:locations.Player.Strenght,
    }
  
   
    navigate('/battle', {state: status})
  };

  const handleDeclineCombat = () => {
    setLocation(null);
    setIsInCombat(false);
  };

  return (
    <div>
      <h2>Параметры персонажа:</h2>
      <ul>
        <li>Имя: {locations.Player.Name}</li>
        <li>Здоровье: {locations.Player.Health}</li>
        <li>Сила: {locations.Player.Strenght}</li>
        <li>Уровень: {locations.Player.Lvl}</li>
        <li>HP: {locations.Player.Hp}</li>
      </ul>
      {location ? (
        <div>
          <p>Вы находитесь в {location[0]}</p>
          <li>Соперник: {npc.Type}</li>
          <li>Здоровье: {npc.Health}</li>
          <li>Сила: {npc.Strenght}</li>
          <li>Уровень: {npc.Lvl}</li>
          <li>Ореаол обитания : {npc.Location}</li>
          {isInCombat ? (
            <p>Вы в бою!</p>
          ) : (
            <div>
              <button onClick={handleAcceptCombat}>Принять бой</button>
              <button onClick={handleDeclineCombat}>Отказаться от боя</button>
            </div>
          )}
        </div>
      ) : (
        <ButtonMove setLocation={setLocation} />
      )}
    </div>
  );
};

export default GamePage;
