// src/GamePage.js
import React, { useState, useEffect } from 'react';
import ButtonMove from './ButtonMove';

const GamePage = () => {
  const player = JSON.parse(localStorage.getItem('player'));
  const [direction, setDirection] = useState(null);

  useEffect(() => {
    if (direction) {
      localStorage.setItem("direction", JSON.stringify(direction));
    }
  }, [direction]);

  const setDirectionWrapper = (newDirection) => {
    setDirection(newDirection);
  };

  return (
    <div>
      <h2>Параметры персонажа:</h2>
      <ul>
        <li>Имя: {player.Player.Name}</li>
        <li>Здоровье: {player.Player.Health}</li>
        <li>Сила: {player.Player.Strenght}</li>
        <li>Уровень: {player.Player.Lvl}</li>
        <li>HP: {player.Player.Hp}</li>
      </ul>
      {direction ? (
        <p>Вы находитесь в {direction.location}</p>
      ) : (
        <ButtonMove setDirection={setDirectionWrapper} />
      )}
    </div>
  );
};

export default GamePage;
